package main

import (
    "big"
    "fmt"
    "os"
    )

func main() {
    var taulu [106][17]byte
    bits := 106 * 17
    taulu[0][0] = 1
    luku := big.NewInt(0)
    lukuteksti := ""
    _, success := luku.SetString(lukuteksti, 10)
    if !success {
        fmt.Print("Luvun tekeminen ep√§onnistu.\n")
        os.Exit(1)
    }
    luku.Div(luku, big.NewInt(17))
    for i := 0; i < 106; i++ {
        for j := 0; j < 17; j++ {
            bits--
            if luku.Bit(bits) == 1 {
                taulu[i][j] = '.'
            } else {
                taulu[i][j] = ' '
            }
        }
    }
    for i := 0; i < 17; i++ {
        for j:= 105; j >= 0; j-- {
            fmt.Printf("%c", taulu[j][i])
        }
        fmt.Println()
    }
}
